import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import { ReactComponent as ArrowLeft } from '../assets/arrow-left.svg';
import { useNavigate } from 'react-router-dom';
const NotePage = () => {
    const { id } = useParams();
    let history = useNavigate();
    let [note, setNote] = useState(null)
    async function handleSubmit(event) {
        event.preventDefault();
        let result = await submitForm(event.target);
        if (result.error) {
            setError(result.error);
        } else {
            history('/');
        }
    }
    useEffect(() => {
        getNote()
    }, [id])

    let getNote = async () => {
        let response = await fetch(`http://127.0.0.1:8000/api/notes/${id}/`)
        let data = await response.json()
        console.log('data:', data)
        setNote(data)
    }
    let updateNote = async () => {
        fetch(`http://127.0.0.1:8000/api/notes/${id}/update/`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(note)
        })
    }
    return (

        <div class='note'>
            <Link to='/'>
                <div class='note-header'><h3><ArrowLeft /></h3></div>
            </Link>
            <textarea onChange={(e) => { { setNote({ ...note, 'body': e.target.value }) } }} defaultValue={note?.text}></textarea>

        </div>
    )
}

export default NotePage
