import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import { ReactComponent as ArrowLeft } from '../assets/arrow-left.svg';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
const NotePage = () => {
    const { id } = useParams();
    let history = useNavigate();
    let [note, setNote] = useState(null)
    let handleSubmit = () => {
        if (id !== 'new') {
            updateNote();
        }
        if (id == 'new' && note !== null) {
            createNote()
        }

    }

    useEffect(() => {
        getNote()
    }, [id])

    let getNote = async () => {
        if (id !== 'new') {
            let response = await fetch(`http://127.0.0.1:8000/api/notes/${id}/`)
            let data = await response.json()

            setNote(data)
        }

    }
    let updateNote = async () => {

        axios.put(`http://127.0.0.1:8000/api/notes/${id}/update/`, {
            text: note.text,

        })
    }
    let createNote = async () => {

        axios.post(`http://127.0.0.1:8000/api/notes/create/`, {

            text: JSON.stringify(note.text),

        })
    }
    let deleteNote = async () => {

        axios.delete(`http://127.0.0.1:8000/api/notes/${id}/delete/`)
        history('/')

    }
    return (

        <div className='note'>
            <Link to='/'>
                <div className='note-header' onClick={(handleSubmit())}><h3><ArrowLeft />

                </h3>
                    {id !== 'new' ? (<button onClick={deleteNote}>Delete</button>) : (<button onClick={handleSubmit}>Add new</button>)}
                </div>

            </Link >
            <textarea onChange={(e) => { { setNote({ ...note, 'text': e.target.value }) } }} defaultValue={note?.text}></textarea>

        </div >
    )
}

export default NotePage
